import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import * as XLSX from 'xlsx'; // Importing the Excel export library

function ViewRecords() {
  const { sessionId } = useParams();
  const [records, setRecords] = useState([]);
  const [sessions, setSessions] = useState([]);
  const [isAllSessionsVisible, setIsAllSessionsVisible] = useState(false);

  useEffect(() => {
    async function fetchRecords() {
      try {
        const res = await axios.get(`http://localhost:5000/api/session-records/${sessionId}`);
        setRecords(res.data);
      } catch (error) {
        console.error(error);
      }
    }

    if (sessionId) {
      fetchRecords();
    } else {
      fetchSessions();
    }
  }, [sessionId]);

  // Fetch all sessions
  const fetchSessions = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/sessions');
      setSessions(res.data);
    } catch (error) {
      console.error(error);
    }
  };

  // Export records to Excel
  const exportToExcel = () => {
    const ws = XLSX.utils.json_to_sheet(records);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, 'Attendance Records');
    XLSX.writeFile(wb, 'Attendance_Records.xlsx');
  };

  // View all sessions
  const handleViewAllSessions = () => {
    setIsAllSessionsVisible(true);
  };

  // Handle session click to view specific session records
  const handleSessionClick = async (sessionId) => {
    try {
      const res = await axios.get(`http://localhost:5000/api/session-records/${sessionId}`);
      setRecords(res.data);
      setIsAllSessionsVisible(false);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '30px' }}>
      <h2>📄 Attendance Records</h2>

      <button
        onClick={exportToExcel}
        style={{
          padding: '10px 20px',
          fontSize: '16px',
          margin: '10px',
          backgroundColor: '#4CAF50',
          color: 'white',
          border: 'none',
          borderRadius: '8px',
          cursor: 'pointer'
        }}
      >
        📥 Export to Excel
      </button>

      <br />

      {!isAllSessionsVisible ? (
        <button
          onClick={handleViewAllSessions}
          style={{
            padding: '10px 20px',
            fontSize: '16px',
            marginTop: '10px',
            backgroundColor: '#007bff',
            color: 'white',
            border: 'none',
            borderRadius: '8px',
            cursor: 'pointer'
          }}
        >
          🔍 View All Sessions
        </button>
      ) : (
        <div>
          <h3>📚 All Sessions</h3>
          <ul style={{ listStyleType: 'none', padding: '0' }}>
            {sessions.map((session) => (
              <li
                key={session.session_id}
                style={{
                  padding: '10px',
                  backgroundColor: '#f0f0f0',
                  margin: '5px',
                  borderRadius: '8px',
                  cursor: 'pointer',
                  fontSize: '18px'
                }}
                onClick={() => handleSessionClick(session.session_id)}
              >
                {session.session_name} - {session.academic_year}
              </li>
            ))}
          </ul>
        </div>
      )}

      {records.length > 0 && !isAllSessionsVisible && (
        <div style={{ marginTop: '30px' }}>
          <h3>Students Present in This Session:</h3>
          <table
            border="1"
            style={{
              margin: 'auto',
              fontSize: '18px',
              width: '80%',
              borderCollapse: 'collapse'
            }}
          >
            <thead style={{ backgroundColor: '#007bff', color: 'white' }}>
              <tr>
                <th>Student Name</th>
                <th>USN</th>
                <th>Department</th>
                <th>Scanned Time</th>
              </tr>
            </thead>
            <tbody>
              {records.map((record) => (
                <tr key={record.record_id}>
                  <td>{record.name}</td>
                  <td>{record.usn}</td>
                  <td>{record.branch}</td>
                  <td>{new Date(record.timestamp).toLocaleString()}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default ViewRecords;
