import React, { useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

function Scan() {
  const { sessionId } = useParams();
  const [id, setId] = useState('');

  const handleScan = async () => {
    try {
      const res = await axios.post('http://localhost:5000/api/scan', { session_id: sessionId, id_number: id });
      alert(`Student Scanned: ${res.data.student_usn}`);
      setId('');
    } catch (error) {
      console.error(error);
      alert('Error scanning student');
    }
  };

  const handleEndSession = async () => {
    try {
      await axios.put(`http://localhost:5000/api/end-session/${sessionId}`);
      alert('Session ended successfully');
      window.location.href = '/dashboard';
    } catch (error) {
      console.error(error);
      alert('Error ending session');
    }
  };

  return (
    <div>
      <h2>Scan Students</h2>
      <input 
        placeholder="Scan Id"
        value={id}
        onChange={e => setId(e.target.value)}
        onKeyDown={e => e.key === 'Enter' && handleScan()}
      /><br/>
      <button onClick={handleScan}>Submit</button>
      <br/>
      <button onClick={handleEndSession} style={{ marginTop: '10px', backgroundColor: 'red', color: 'white' }}>
        End Session
      </button>
    </div>
  );
}

export default Scan;
