import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ViewRecords() {
  const [sessions, setSessions] = useState([]);
  const [selectedSession, setSelectedSession] = useState('');
  const [records, setRecords] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/sessions')
      .then(res => setSessions(res.data))
      .catch(err => console.error(err));
  }, []);

  const fetchRecords = () => {
    if (!selectedSession) return;
    axios.get(`http://localhost:5000/api/records/${selectedSession}`)
      .then(res => setRecords(res.data))
      .catch(err => console.error(err));
  };

  return (
    <div>
      <h2>View Attendance Records</h2>
      <select onChange={(e) => setSelectedSession(e.target.value)} value={selectedSession}>
        <option value="">Select Session</option>
        {sessions.map(session => (
          <option key={session.session_id} value={session.session_id}>{session.session_name}</option>
        ))}
      </select>
      <button onClick={fetchRecords}>View</button>

      {records.length > 0 && (
        <table border="1" style={{ marginTop: '20px' }}>
          <thead>
            <tr>
              <th>Student Name</th>
              <th>USN</th>
              <th>Department</th>
              <th>Scanned Time</th>
            </tr>
          </thead>
          <tbody>
            {records.map(record => (
              <tr key={record.record_id}>
                <td>{record.name}</td>
                <td>{record.usn}</td>
                <td>{record.branch}</td>
                <td>{record.timestamp}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default ViewRecords;
