import React, { useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import * as XLSX from 'xlsx';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function Scan() {
  const { sessionId } = useParams();
  const navigate = useNavigate();
  const [id, setId] = useState('');
  const [scannedStudents, setScannedStudents] = useState([]);
  const [exported, setExported] = useState(false);

  const handleScan = async () => {
    if (!id.trim()) return;

    // Check if student has already been scanned
    const isAlreadyScanned = scannedStudents.some(student => student.student_usn === id);
    if (isAlreadyScanned) {
      toast.warn('❌ This student has already been marked.', { autoClose: 3000 });
      return;
    }

    try {
      const res = await axios.post('http://localhost:5000/api/scan', {
        session_id: sessionId,
        id_number: id,
      });

      toast.success(`✅ Scanned: ${res.data.student_usn}`, { autoClose: 2000 });
      setScannedStudents(prev => [...prev, res.data]);
      setId('');
    } catch (error) {
      console.error(error);
      toast.error('❌ Error scanning student', { autoClose: 3000 });
    }
  };

  const handleEndSession = async () => {
    const confirmEnd = window.confirm(
      "Are you sure you want to end the session? Please make sure you've exported the student data."
    );
    if (confirmEnd) {
      if (!exported) {
        const exportConfirm = window.confirm(
          "You haven't exported the Excel file yet. Do you want to export it now?"
        );
        if (exportConfirm) {
          handleExport();
        }
      }
      try {
        await axios.put(`http://localhost:5000/api/end-session/${sessionId}`);
        toast.success('🛑 Session ended successfully', { autoClose: 2000 });
        setTimeout(() => navigate('/dashboard'), 2000);
      } catch (error) {
        console.error(error);
        toast.error('❌ Error ending session', { autoClose: 3000 });
      }
    }
  };

  const handleLogout = () => {
    const confirmLogout = window.confirm(
      "Are you sure you want to log out? Please make sure you've exported the student data."
    );
    if (confirmLogout) {
      if (!exported) {
        const exportConfirm = window.confirm(
          "You haven't exported the Excel file yet. Do you want to export it now?"
        );
        if (exportConfirm) {
          handleExport();
        }
      }
      localStorage.clear();
      navigate('/');
    }
  };

  const handleExport = () => {
    if (scannedStudents.length === 0) {
      toast.warn('⚠️ No students to export', { autoClose: 2000 });
      return;
    }

    const dataToExport = scannedStudents.map((student, index) => ({
      No: index + 1,
      Name: student.student_name,
      USN: student.student_usn,
      Department: student.student_department,
      Time: student.timestamp,
    }));

    const worksheet = XLSX.utils.json_to_sheet(dataToExport);
    const workbook = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(workbook, worksheet, "ScannedStudents");
    XLSX.writeFile(workbook, "Scanned_Students.xlsx");

    toast.success('📄 Excel file exported!', { autoClose: 2000 });
    setExported(true);  // Mark as exported
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '50px' }}>
      <ToastContainer position="top-center" />

      <h2>📷 Scan Students</h2>

      <input
        style={inputStyle}
        placeholder="Scan Student ID Number"
        value={id}
        onChange={(e) => setId(e.target.value)}
        onKeyDown={(e) => e.key === 'Enter' && handleScan()}
      />
      <br /><br />

      <button onClick={handleScan} style={buttonStyle}>✅ Scan</button> &nbsp;
      <button onClick={handleEndSession} style={{ ...buttonStyle, backgroundColor: 'orange' }}>🛑 End Session</button> <br /><br />
      <button onClick={handleExport} style={{ ...buttonStyle, backgroundColor: 'green' }}>📄 Export to Excel</button> &nbsp;
      <button onClick={handleLogout} style={{ ...buttonStyle, backgroundColor: 'red' }}>🚪 Logout</button>

      <div style={{ marginTop: '40px' }}>
        <h3>📝 Scanned Students</h3>
        {scannedStudents.length === 0 ? (
          <p>No students scanned yet.</p>
        ) : (
          <table style={tableStyle}>
            <thead>
              <tr>
                <th>No</th>
                <th>Name</th>
                <th>USN</th>
                <th>Department</th>
                <th>Timestamp</th>
              </tr>
            </thead>
            <tbody>
              {scannedStudents.map((student, index) => (
                <tr key={index}>
                  <td>{index + 1}</td>
                  <td>{student.student_name}</td>
                  <td>{student.student_usn}</td>
                  <td>{student.student_department}</td>
                  <td>{student.timestamp}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

// Styles
const inputStyle = {
  padding: '10px',
  width: '300px',
  fontSize: '16px',
};

const buttonStyle = {
  padding: '10px 20px',
  fontSize: '16px',
  margin: '5px',
  border: 'none',
  borderRadius: '8px',
  backgroundColor: '#007bff',
  color: 'white',
  cursor: 'pointer',
};

const tableStyle = {
  margin: 'auto',
  marginTop: '20px',
  borderCollapse: 'collapse',
  width: '80%',
};

export default Scan;
