import axios from 'axios';
import { useParams } from 'react-router-dom';

function Scan() {
  const { sessionId } = useParams();
  const [id, setId] = useState('');

  const handleScan = async () => {
    try {
      await axios.post('http://localhost:5000/api/scan', { session_id: sessionId, student_usn: usn });
      alert('Student Scanned');
      setUsn('');
    } catch (error) {
      console.error(error);
      alert('Error scanning student');
    }
  };

  return (
    <div>
      <h2>Scan Students</h2>
      <input 
        placeholder="Scan or Enter USN"
        value={usn}
        onChange={e => setUsn(e.target.value)}
        onKeyDown={e => e.key === 'Enter' && handleScan()}
      /><br/>
      <button onClick={handleScan}>Submit</button>
    </div>
  );
}

export default Scan;
