import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import * as XLSX from 'xlsx';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import '../App.css';

function Scan() {
  const { sessionId } = useParams();
  const navigate = useNavigate();
  const [barcode, setBarcode] = useState('');
  const [scannedStudents, setScannedStudents] = useState([]);
  const [exported, setExported] = useState(false);

  useEffect(() => {
    const handleKeyPress = (e) => {
      if (e.key === 'Enter') {
        handleScan(barcode.trim());
        setBarcode('');
      } else {
        setBarcode((prev) => prev + e.key);
      }
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, [barcode]);

  const handleScan = async (id) => {
    if (!id) return;

    const exists = scannedStudents.find((s) => s.student_usn === id);
    if (exists) {
      toast.warn('‚ö†Ô∏è Already Scanned');
      return;
    }

    try {
      const res = await axios.post('http://localhost:5000/api/scan', {
        session_id: sessionId,
        id_number: id,
      });
      setScannedStudents((prev) => [...prev, res.data]);
      toast.success(`‚úÖ Scanned: ${res.data.student_usn}`);
    } catch (err) {
      if (err.response?.status === 409) {
        toast.warn('‚ö†Ô∏è Student already scanned');
      } else if (err.response?.status === 404) {
        toast.error('‚ùå Student not found');
      } else {
        toast.error('‚ùå Scan error');
      }
    }
  };

  const handleExport = () => {
    if (scannedStudents.length === 0) {
      toast.warn('‚ö†Ô∏è No students to export');
      return;
    }

    const data = scannedStudents.map((s, i) => ({
      No: i + 1,
      Name: s.student_name,
      USN: s.student_usn,
      Department: s.student_department,
      Time: s.timestamp,
    }));

    const sheet = XLSX.utils.json_to_sheet(data);
    const book = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(book, sheet, 'ScannedStudents');
    XLSX.writeFile(book, 'attendance.xlsx');

    setExported(true);
    toast.success('üìÑ Exported to Excel');
  };

  const handleEndSession = async () => {
    if (!exported && scannedStudents.length > 0) handleExport();
    try {
      await axios.put(`http://localhost:5000/api/end-session/${sessionId}`);
      toast.success('üõë Session ended');
      setTimeout(() => navigate('/dashboard'), 2000);
    } catch {
      toast.error('‚ùå Error ending session');
    }
  };

  const handleLogout = () => {
    if (!exported && scannedStudents.length > 0) handleExport();
    setTimeout(() => {
      localStorage.clear();
      navigate('/');
    }, 1500);
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '40px' }}>
      <ToastContainer position="top-center" />
      <header>
      <img src="logo-placeholder.png" alt="Logo" />
      <h1>BMS Institute of Technology and Management</h1>
</header>
      <h2>üì∑ Scan Students</h2>
      <p style={{ color: 'gray' }}>Use scanner ‚Äî no manual input required</p>

      <button onClick={handleEndSession} style={{ ...buttonStyle, backgroundColor: 'orange' }}>üõë End Session</button> &nbsp;
      <button onClick={handleExport} style={{ ...buttonStyle, backgroundColor: 'green' }}>üìÑ Export</button> &nbsp;
      <button onClick={handleLogout} style={{ ...buttonStyle, backgroundColor: 'red' }}>üö™ Logout</button>

      <div style={{ marginTop: '40px' }}>
        <h3>üìù Scanned Students</h3>
        {scannedStudents.length === 0 ? (
          <p>No students scanned yet.</p>
        ) : (
          <table style={tableStyle}>
            <thead>
              <tr>
                <th>No</th>
                <th>Name</th>
                <th>USN</th>
                <th>Department</th>
                <th>Timestamp</th>
              </tr>
            </thead>
            <tbody>
              {scannedStudents.map((s, i) => (
                <tr key={i}>
                  <td>{i + 1}</td>
                  <td>{s.student_name}</td>
                  <td>{s.student_usn}</td>
                  <td>{s.student_department}</td>
                  <td>{s.timestamp}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

// Styles
const buttonStyle = {
  padding: '10px 20px',
  fontSize: '16px',
  margin: '5px',
  border: 'none',
  borderRadius: '8px',
  color: 'white',
  backgroundColor: '#007bff',
  cursor: 'pointer',
};

const tableStyle = {
  margin: 'auto',
  marginTop: '20px',
  borderCollapse: 'collapse',
  width: '85%',
  border: '1px solid #ccc',
};

export default Scan;
