import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import logo from '../images/logo.png';

function Dashboard() {
  const [sessions, setSessions] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchSessions = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get('http://localhost:5000/api/sessions', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setSessions(res.data);
      } catch (error) {
        console.error(error);
        alert('Failed to load sessions');
      }
    };
    fetchSessions();
  }, []);

  const handleCreateSession = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.post('http://localhost:5000/api/session', {}, {
        headers: { Authorization: `Bearer ${token}` },
      });
      const session_id = res.data.session_id;
      navigate(`/scan/${session_id}`);
    } catch (error) {
      console.error(error);
      alert('Could not create session');
    }
  };

  return (
    <div style={styles.page}>
      <img src={logo} alt="Logo" style={styles.logo} />
      <h2 style={styles.title}>Department Dashboard</h2>
      <div style={styles.card}>
        <button onClick={handleCreateSession} style={styles.button}>Create New Session</button>
        <h3 style={styles.subTitle}>Past Sessions</h3>
        <ul style={styles.list}>
          {sessions.map((session, index) => (
            <li key={index} style={styles.listItem}>
              <span>Session ID: {session.session_id}</span>
              <button style={styles.viewButton} onClick={() => navigate(`/scan/${session.session_id}`)}>View</button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

const styles = {
  page: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    padding: '40px 20px',
    backgroundColor: '#f5f7fa',
    minHeight: '100vh',
    fontFamily: "'Segoe UI', sans-serif",
  },
  logo: {
    width: '220px',
    marginBottom: '10px',
  },
  title: {
    fontSize: '26px',
    color: '#0b3558',
    marginBottom: '30px',
  },
  card: {
    width: '100%',
    maxWidth: '600px',
    background: '#fff',
    padding: '30px',
    borderRadius: '12px',
    boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
  },
  button: {
    padding: '12px 20px',
    backgroundColor: '#28a745',
    color: '#fff',
    fontWeight: '600',
    fontSize: '16px',
    border: 'none',
    borderRadius: '6px',
    cursor: 'pointer',
    marginBottom: '20px',
  },
  subTitle: {
    margin: '10px 0',
    fontSize: '18px',
    color: '#333',
  },
  list: {
    listStyle: 'none',
    padding: 0,
  },
  listItem: {
    padding: '10px 15px',
    borderBottom: '1px solid #eee',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
  },
  viewButton: {
    backgroundColor: '#007bff',
    color: '#fff',
    padding: '8px 14px',
    border: 'none',
    borderRadius: '5px',
    fontSize: '14px',
    cursor: 'pointer',
  },
};

export default Dashboard;
