import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import logo from '../images/logo.png';

function Scan() {
  const { session_id } = useParams();
  const [students, setStudents] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchScanned = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get(`http://localhost:5000/api/scanned/${session_id}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setStudents(res.data);
      } catch (error) {
        console.error(error);
        alert('Error fetching scanned students');
      }
    };
    fetchScanned();
  }, [session_id]);

  const handleEndSession = () => {
    navigate('/dashboard');
  };

  return (
    <div style={styles.page}>
      <img src={logo} alt="Logo" style={styles.logo} />
      <h2 style={styles.title}>Scanned Students</h2>
      <div style={styles.tableWrapper}>
        <table style={styles.table}>
          <thead>
            <tr>
              <th style={styles.th}>USN</th>
              <th style={styles.th}>Name</th>
              <th style={styles.th}>Time</th>
            </tr>
          </thead>
          <tbody>
            {students.map((s, i) => (
              <tr key={i}>
                <td style={styles.td}>{s.usn}</td>
                <td style={styles.td}>{s.name}</td>
                <td style={styles.td}>{s.timestamp}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <button style={styles.button} onClick={handleEndSession}>End Session</button>
    </div>
  );
}

const styles = {
  page: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    padding: '40px 20px',
    minHeight: '100vh',
    backgroundColor: '#f5f7fa',
    fontFamily: "'Segoe UI', sans-serif",
  },
  logo: {
    width: '220px',
    marginBottom: '10px',
  },
  title: {
    fontSize: '24px',
    color: '#222',
    margin: '20px 0',
  },
  tableWrapper: {
    width: '100%',
    maxWidth: '700px',
    overflowX: 'auto',
    marginBottom: '30px',
    backgroundColor: '#fff',
    boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
    borderRadius: '8px',
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse',
  },
  th: {
    padding: '12px',
    backgroundColor: '#0b3558',
    color: '#fff',
    textAlign: 'left',
    borderTopLeftRadius: '8px',
    borderTopRightRadius: '8px',
  },
  td: {
    padding: '12px',
    borderBottom: '1px solid #ccc',
    fontSize: '15px',
  },
  button: {
    padding: '12px 20px',
    backgroundColor: '#2196F3',
    color: '#fff',
    fontWeight: '600',
    fontSize: '16px',
    border: 'none',
    borderRadius: '6px',
    cursor: 'pointer',
  },
};

export default Scan;
