import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../App.css';



function Dashboard() {
  const [sessionName, setSessionName] = useState('');
  const navigate = useNavigate();

  const createSession = async () => {
    if (!sessionName ) {
      alert('Please fill in all the fields!');
      return;
    }
    try {
      const token = localStorage.getItem('token');
      const payload = {
        session_name: sessionName,
        started_by: 1
      };
      const res = await axios.post('http://localhost:5000/api/session', payload, {
        headers: { Authorization: `Bearer ${token}` }
      });
      navigate(`/scan/${res.data.session_id}`);
    } catch (error) {
      console.error(error);
      alert('Error creating session');
    }
  };

  const handleLogout = () => {
    // Clear localStorage to log out the user
    localStorage.removeItem('token');
    // Navigate to login page
    navigate('/');
  };

  return (
    <div></div>
    
  );
}

const styles = {
  container: {
    minHeight: '100vh',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    background: '#f2f2f2',
    padding: '20px',
  },
  card: {
    background: '#fff',
    padding: '30px',
    borderRadius: '12px',
    boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
    width: '100%',
    maxWidth: '400px',
    textAlign: 'center'
  },
  title: {
    marginBottom: '20px',
    color: '#333'
  },
  input: {
    width: '100%',
    padding: '10px',
    margin: '10px 0',
    borderRadius: '6px',
    border: '1px solid #ccc',
    fontSize: '16px'
  },
  buttonPrimary: {
    width: '100%',
    padding: '10px',
    margin: '10px 0',
    border: 'none',
    borderRadius: '6px',
    backgroundColor: '#4CAF50',
    color: '#fff',
    fontSize: '16px',
    cursor: 'pointer'
  },
  buttonSecondary: {
    width: '100%',
    padding: '10px',
    border: 'none',
    borderRadius: '6px',
    backgroundColor: '#2196F3',
    color: '#fff',
    fontSize: '16px',
    cursor: 'pointer'
  }
};

export default Dashboard;
