import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import * as XLSX from 'xlsx';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import '../App.css';


function Scan() {
  const { sessionId } = useParams();
  const navigate = useNavigate();
  const [barcode, setBarcode] = useState('');
  const [scannedStudents, setScannedStudents] = useState([]);
  const [exported, setExported] = useState(false);

  useEffect(() => {
    const handleKeyPress = (e) => {
      if (e.key === 'Enter') {
        handleScan(barcode.trim());
        setBarcode('');
      } else {
        setBarcode((prev) => prev + e.key);
      }
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, [barcode]);

  const handleScan = async (id) => {
    if (!id) return;

    const exists = scannedStudents.find((s) => s.student_usn === id);
    if (exists) {
      toast.warn('⚠️ Already Scanned');
      return;
    }

    try {
      const res = await axios.post('http://localhost:5000/api/scan', {
        session_id: sessionId,
        id_number: id,
      });
      setScannedStudents((prev) => [...prev, res.data]);
      toast.success(`✅ Scanned: ${res.data.student_usn}`);
    } catch (err) {
      if (err.response?.status === 409) {
        toast.warn('⚠️ Student already scanned');
      } else if (err.response?.status === 404) {
        toast.error('❌ Student not found');
      } else {
        toast.error('❌ Scan error');
      }
    }
  };

  const handleExport = () => {
    if (scannedStudents.length === 0) {
      toast.warn('⚠️ No students to export');
      return;
    }

    const data = scannedStudents.map((s, i) => ({
      No: i + 1,
      Name: s.student_name,
      USN: s.student_usn,
      Department: s.student_department,
      Time: s.timestamp,
    }));

    const sheet = XLSX.utils.json_to_sheet(data);
    const book = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(book, sheet, 'ScannedStudents');
    XLSX.writeFile(book, 'attendance.xlsx');

    setExported(true);
    toast.success('📄 Exported to Excel');
  };

  const handleEndSession = async () => {
    if (!exported && scannedStudents.length > 0) handleExport();
    try {
      await axios.put(`http://localhost:5000/api/end-session/${sessionId}`);
      toast.success('🛑 Session ended');
      setTimeout(() => navigate('/dashboard'), 2000);
    } catch {
      toast.error('❌ Error ending session');
    }
  };

  const handleLogout = () => {
    if (!exported && scannedStudents.length > 0) handleExport();
    setTimeout(() => {
      localStorage.clear();
      navigate('/');
    }, 1500);
  };

  return (
    <div>
      
    </div>
    
  );
}

// Styles
const buttonStyle = {
  padding: '10px 20px',
  fontSize: '16px',
  margin: '5px',
  border: 'none',
  borderRadius: '8px',
  color: 'white',
  backgroundColor: '#007bff',
  cursor: 'pointer',
};

const tableStyle = {
  margin: 'auto',
  marginTop: '20px',
  borderCollapse: 'collapse',
  width: '85%',
  border: '1px solid #ccc',
};

export default Scan;
