import React, { useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

function Scan() {
  const { sessionId } = useParams();
  const navigate = useNavigate();
  const [id, setId] = useState('');

  const handleScan = async () => {
    try {
      const res = await axios.post('http://localhost:5000/api/scan', { session_id: sessionId, id_number: id });
      alert(`Student Scanned: USN - ${res.data.student_usn}`);
      setId('');
    } catch (error) {
      console.error(error);
      alert('Error scanning student');
    }
  };

  const handleEndSession = async () => {
    try {
      await axios.put(`http://localhost:5000/api/end-session/${sessionId}`);
      alert('Session ended successfully');
      navigate('/dashboard');
    } catch (error) {
      console.error(error);
      alert('Error ending session');
    }
  };

  const handleLogout = () => {
    localStorage.clear();
    navigate('/');
  };

  const handleViewRecords = () => {
    navigate(`/view-records/${sessionId}`);
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '50px' }}>
      <h2>ðŸ“· Scan Students</h2>

      <input 
        style={{ padding: '10px', width: '300px', fontSize: '16px' }}
        placeholder="Scan Student ID Number"
        value={id}
        onChange={e => setId(e.target.value)}
        onKeyDown={e => e.key === 'Enter' && handleScan()}
      />
      <br/><br/>

      <button onClick={handleScan} style={buttonStyle}>âœ… Scan</button> &nbsp;
      <button onClick={handleEndSession} style={{...buttonStyle, backgroundColor: 'orange'}}>ðŸ›‘ End Session</button> <br/><br/>
      <button onClick={handleViewRecords} style={{...buttonStyle, backgroundColor: 'green'}}>ðŸ“„ View Records</button> &nbsp;
      <button onClick={handleLogout} style={{...buttonStyle, backgroundColor: 'red'}}>ðŸšª Logout</button>
    </div>
  );
}

const buttonStyle = {
  padding: '10px 20px',
  fontSize: '16px',
  margin: '5px',
  border: 'none',
  borderRadius: '8px',
  backgroundColor: '#007bff',
  color: 'white',
  cursor: 'pointer'
};

export default Scan;
