import React, { useEffect, useState } from "react";
import axios from "axios";
import * as XLSX from "xlsx";
import { useNavigate } from "react-router-dom";
import { toast } from "react-toastify";

const Scan = () => {
  const navigate = useNavigate();
  const [scannedStudents, setScannedStudents] = useState([]);
  const [manualScan, setManualScan] = useState(false);
  const [manualID, setManualID] = useState("");
  const [exported, setExported] = useState(false);

  // ✅ Optional: Session check (uncomment if needed)
  // useEffect(() => {
  //   const user = localStorage.getItem("name");
  //   if (!user) {
  //     toast.error("Session expired");
  //     navigate("/");
  //   }
  // }, []);

  useEffect(() => {
    let barcode = "";
    const handleKeyPress = (e) => {
      if (e.key === "Enter") {
        if (barcode.trim()) {
          handleScan(barcode.trim());
          barcode = "";
        }
      } else {
        barcode += e.key;
      }
    };
    window.addEventListener("keydown", handleKeyPress);
    return () => window.removeEventListener("keydown", handleKeyPress);
  }, []);

  const handleScan = async (id_number) => {
    if (scannedStudents.some((s) => s.id_number === id_number)) {
      toast.warning("Already Scanned");
      return;
    }

    try {
      const res = await axios.post("http://localhost:3001/scan", { id_number });
      if (res.data.success) {
        setScannedStudents((prev) => [...prev, res.data.student]);
        toast.success("Scanned Successfully");
      } else {
        toast.error("Student not found");
      }
    } catch (err) {
      toast.error("Error scanning student");
    }
  };

  const handleManualSubmit = (e) => {
    e.preventDefault();
    if (!manualID.trim()) {
      toast.warning("Please enter a valid ID");
      return;
    }
    handleScan(manualID.trim());
    setManualID("");
  };

  const exportToExcel = () => {
    if (scannedStudents.length === 0) {
      toast.warning("No students to export");
      return;
    }
    const worksheet = XLSX.utils.json_to_sheet(scannedStudents);
    const workbook = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(workbook, worksheet, "Scanned Students");
    XLSX.writeFile(workbook, "scanned_students.xlsx");
    setExported(true);
    toast.success("Exported successfully");
  };

  const handleEndSession = () => {
    if (!exported) exportToExcel();
    setScannedStudents([]);
    setExported(false);
    toast.info("Session ended");
  };

  const handleLogout = () => {
    if (!exported) exportToExcel();
    localStorage.clear();
    setExported(false);
    navigate("/");
  };

  const handleDelete = (usnToDelete) => {
    const updatedList = scannedStudents.filter((s) => s.student_usn !== usnToDelete);
    setScannedStudents(updatedList);
    toast.info("Student removed");
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h2>Barcode Scanner</h2>

      <button onClick={() => setManualScan((prev) => !prev)}>
        {manualScan ? "Hide Manual Entry" : "Enter Manually"}
      </button>

      {manualScan && (
        <form onSubmit={handleManualSubmit} style={{ marginTop: "10px" }}>
          <input
            type="text"
            value={manualID}
            onChange={(e) => setManualID(e.target.value)}
            placeholder="Enter ID"
          />
          <button type="submit">Submit</button>
        </form>
      )}

      <div style={{ marginTop: "20px" }}>
        <button onClick={exportToExcel}>Export to Excel</button>
        <button onClick={handleEndSession} style={{ marginLeft: "10px" }}>
          End Session
        </button>
        <button onClick={handleLogout} style={{ marginLeft: "10px" }}>
          Logout
        </button>
      </div>

      <div style={{ marginTop: "30px" }}>
        <h3>Scanned Students</h3>
        {scannedStudents.length === 0 ? (
          <p>No students scanned yet.</p>
        ) : (
          <div style={{ maxHeight: "300px", overflowY: "auto", border: "1px solid #ccc" }}>
            <table style={{ width: "100%", borderCollapse: "collapse" }}>
              <thead>
                <tr>
                  <th>Name</th>
                  <th>USN</th>
                  <th>Section</th>
                  <th>Branch</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
                {scannedStudents.map((s) => (
                  <tr key={s.student_usn}>
                    <td>{s.student_name}</td>
                    <td>{s.student_usn}</td>
                    <td>{s.section}</td>
                    <td>{s.branch}</td>
                    <td>
                      <button onClick={() => handleDelete(s.student_usn)}>Remove</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default Scan;
