import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import * as XLSX from 'xlsx';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import '../App.css';
import logo from '../images/logo.png';

function Scan() {
  const { sessionId } = useParams();
  const navigate = useNavigate();
  const [barcode, setBarcode] = useState('');
  const [scannedStudents, setScannedStudents] = useState([]);
  const [exported, setExported] = useState(false);
  const [showManualSection, setShowManualSection] = useState(false);
  const [manualId, setManualId] = useState('');

  useEffect(() => {
    const handleKeyPress = (e) => {
      if (showManualSection && e.target.tagName === 'INPUT') {
        if (e.key === 'Enter') handleManualScan();
        return;
      }

      if (e.key === 'Enter') {
        handleScan(barcode.trim());
        setBarcode('');
      } else {
        setBarcode((prev) => prev + e.key);
      }
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, [barcode, showManualSection, manualId]);

  const handleScan = async (id) => {
    if (!id) return;
    if (scannedStudents.find((s) => s.student_usn === id)) {
      toast.warn('⚠️ Already Scanned');
      return;
    }

    try {
      const res = await axios.post('http://localhost:5000/api/scan', {
        session_id: sessionId,
        id_number: id
      });
      setScannedStudents((prev) => [...prev, res.data]);
      toast.success(`✅ Scanned: ${res.data.student_usn}`);
    } catch (err) {
      if (err.response?.status === 409) toast.warn('⚠️ Student already scanned');
      else if (err.response?.status === 404) toast.error('❌ Student not found');
      else toast.error('❌ Scan error');
    }
  };

  const handleExport = () => {
    if (scannedStudents.length === 0) return toast.warn('⚠️ No students to export');
    const data = scannedStudents.map((s, i) => ({
      No: i + 1,
      Name: s.student_name,
      USN: s.student_usn,
      Department: s.student_department,
      Time: s.timestamp
    }));
    const sheet = XLSX.utils.json_to_sheet(data);
    const book = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(book, sheet, 'ScannedStudents');
    XLSX.writeFile(book, 'attendance.xlsx');
    setExported(true);
    toast.success('📄 Exported to Excel');
  };

  const handleEndSession = async () => {
    if (!exported && scannedStudents.length > 0) handleExport();
    try {
      await axios.put(`http://localhost:5000/api/end-session/${sessionId}`);
      toast.success('🛑 Session ended');
      setTimeout(() => navigate('/dashboard'), 2000);
    } catch {
      toast.error('❌ Error ending session');
    }
  };

  const handleLogout = () => {
    if (!exported && scannedStudents.length > 0) handleExport();
    setTimeout(() => {
      localStorage.clear();
      navigate('/');
    }, 1500);
  };

  const toggleManualSection = () => setShowManualSection((prev) => !prev);
  const handleManualScan = () => {
    const trimmed = manualId.trim();
    if (!trimmed) return toast.warn('Enter a valid ID or USN');
    handleScan(trimmed);
    setManualId('');
  };

  const handleDeleteStudent = async (usn) => {
    try {
      await axios.delete('http://localhost:5000/api/attendance', {
        data: { session_id: sessionId, student_usn: usn }
      });
      setScannedStudents((prev) => prev.filter((s) => s.student_usn !== usn));
      toast.info(`🗑️ Deleted: ${usn}`);
    } catch {
      toast.error('❌ Failed to delete attendance');
    }
  };

  return (
    <div style={styles.page}>
      <ToastContainer position="top-center" />

      <img src={logo} alt="Logo" style={styles.logo} />
      <h2 style={styles.heading}>Placement Department Barcode Attendance</h2>

      <div style={styles.controls}>
        <button onClick={handleEndSession} style={{ ...styles.button, backgroundColor: '#ff9800' }}>🛑 End Session</button>
        <button onClick={handleExport} style={{ ...styles.button, backgroundColor: '#4CAF50' }}>📄 Export</button>
        <button onClick={handleLogout} style={{ ...styles.button, backgroundColor: '#f44336' }}>🚪 Logout</button>
        <button onClick={toggleManualSection} style={{ ...styles.button, backgroundColor: '#607d8b' }}>
          ✍️ {showManualSection ? 'Hide Manual Entry' : 'Manual Entry'}
        </button>
      </div>

      {showManualSection && (
        <div style={styles.manualEntry}>
          <input
            type="text"
            placeholder="Enter USN or ID"
            value={manualId}
            onChange={(e) => setManualId(e.target.value)}
            style={styles.input}
          />
          <button onClick={handleManualScan} style={{ ...styles.button, backgroundColor: '#2196F3' }}>➕ Add</button>
        </div>
      )}

      <div style={styles.tableSection}>
        <h3 style={styles.subheading}>📝 Scanned Students</h3>
        {scannedStudents.length === 0 ? (
          <p>No students scanned yet.</p>
        ) : (
          <table style={styles.table}>
            <thead>
              <tr>
                <th>No</th>
                <th>Name</th>
                <th>USN</th>
                <th>Department</th>
                <th>Timestamp</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {scannedStudents.map((s, i) => (
                <tr key={i}>
                  <td>{i + 1}</td>
                  <td>{s.student_name}</td>
                  <td>{s.student_usn}</td>
                  <td>{s.student_department}</td>
                  <td>{s.timestamp}</td>
                  <td>
                    <button
                      style={styles.deleteButton}
                      onClick={() => handleDeleteStudent(s.student_usn)}
                    >
                      ❌
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

const styles = {
  page: {
    padding: '40px 20px',
    backgroundColor: '#f5f7fa',
    fontFamily: "'Segoe UI', sans-serif",
    minHeight: '100vh',
    textAlign: 'center',
  },
  logo: {
    width: '220px',
    marginBottom: '10px',
  },
  heading: {
    fontSize: '24px',
    color: '#222',
    marginBottom: '30px',
  },
  controls: {
    display: 'flex',
    justifyContent: 'center',
    flexWrap: 'wrap',
    gap: '10px',
    marginBottom: '20px',
  },
  button: {
    padding: '10px 20px',
    border: 'none',
    color: '#fff',
    borderRadius: '6px',
    fontWeight: '600',
    cursor: 'pointer',
    fontSize: '14px',
  },
  manualEntry: {
    marginBottom: '30px',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    gap: '10px',
  },
  input: {
    padding: '10px',
    fontSize: '16px',
    width: '250px',
    border: '1px solid #ccc',
    borderRadius: '6px',
  },
  tableSection: {
    marginTop: '20px',
  },
  subheading: {
    fontSize: '18px',
    marginBottom: '10px',
  },
  table: {
    width: '100%',
    maxWidth: '1000px',
    margin: '0 auto',
    borderCollapse: 'collapse',
    backgroundColor: '#fff',
    boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
    borderRadius: '6px',
    overflow: 'hidden',
  },
  deleteButton: {
    backgroundColor: 'red',
    color: '#fff',
    border: 'none',
    padding: '6px 10px',
    borderRadius: '4px',
    cursor: 'pointer',
  }
};

export default Scan;
