import React, { useState, useEffect } from "react";
import axios from "axios";

const ViewRecords = () => {
  const [sessions, setSessions] = useState([]);
  const [selectedSession, setSelectedSession] = useState("");
  const [records, setRecords] = useState([]);

  useEffect(() => {
    // Fetch all sessions (you may need to fetch this from your backend)
    axios.get('/sessions').then(response => {
      setSessions(response.data);
    });
  }, []);

  const handleViewRecords = async () => {
    try {
      const response = await axios.get(`/view-records/${selectedSession}`);
      setRecords(response.data);
    } catch (error) {
      alert("Error fetching records");
    }
  };

  return (
    <div>
      <h2>View Session Records</h2>
      <select onChange={(e) => setSelectedSession(e.target.value)}>
        <option value="">Select a Session</option>
        {sessions.map((session) => (
          <option key={session.session_id} value={session.session_id}>
            {session.session_name}
          </option>
        ))}
      </select>
      <button onClick={handleViewRecords}>View Records</button>

      <table>
        <thead>
          <tr>
            <th>Student Name</th>
            <th>USN</th>
            <th>Department</th>
            <th>Scanned Time</th>
          </tr>
        </thead>
        <tbody>
          {records.map((record) => (
            <tr key={record.record_id}>
              <td>{record.name}</td>
              <td>{record.id_number}</td>
              <td>{record.department}</td>
              <td>{new Date(record.timestamp).toLocaleString()}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ViewRecords;
