import React, { useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

function Scan() {
  const { sessionId } = useParams();
  const [idNumber, setIdNumber] = useState('');

  const handleScan = async () => {
    try {
      const response = await axios.post('http://localhost:5000/api/scan', { session_id: sessionId, id_number: idNumber });
      const scannedUSN = response.data.student_usn; // assuming backend sends it

      alert(`Student Scanned Successfully!\nUSN: ${scannedUSN}`);
      setIdNumber('');
    } catch (error) {
      console.error(error);
      alert('Error scanning student');
    }
  };

  return (
    <div>
      <h2>Scan Students</h2>
      <input 
        placeholder="Scan ID Number"
        value={idNumber}
        onChange={e => setIdNumber(e.target.value)}
        onKeyDown={e => e.key === 'Enter' && handleScan()}
      /><br/>
      <button onClick={handleScan}>Submit</button>
      <button onClick={handleEndSession}>End Session</button>

    </div>
    
  );
}

export default Scan;
