import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Dashboard() {
  const [sessionName, setSessionName] = useState('');
  const [department, setDepartment] = useState('');
  const [academicYear, setAcademicYear] = useState('');
  const navigate = useNavigate();

  const createSession = async () => {
    try {
      const token = localStorage.getItem('token');
      const payload = {
        session_name: sessionName,
        department,
        academic_year: academicYear,
        started_by: 1
      };
      const res = await axios.post('http://localhost:5000/api/session', payload, {
        headers: { Authorization: `Bearer ${token}` }
      });
      navigate(`/scan/${res.data.session_id}`);
    } catch (error) {
      console.error(error);
      alert('Error creating session');
    }
  };

  return (
    <div>
      <h2>Create Session</h2>
      <input placeholder="Session Name" onChange={e => setSessionName(e.target.value)} /><br/>
      <input placeholder="Department" onChange={e => setDepartment(e.target.value)} /><br/>
      <select onChange={e => setAcademicYear(e.target.value)}>
        <option value="">Select Academic Year</option>
        <option value="2022">2022</option>
        <option value="2023">2023</option>
        <option value="2024">2024</option>
      </select><br/>
      <button onClick={createSession}>Start Scanning</button>
    </div>
  );
}

export default Dashboard;
