import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Dashboard() {
  const [sessionName, setSessionName] = useState('');
  const [department, setDepartment] = useState('');
  const [academicYear, setAcademicYear] = useState('');
  const navigate = useNavigate();

  const createSession = async () => {
    if (!sessionName || !department || !academicYear) {
      alert('Please fill in all the fields!');
      return;
    }
    try {
      const token = localStorage.getItem('token');
      const payload = {
        session_name: sessionName,
        department,
        academic_year: academicYear,
        started_by: 1
      };
      const res = await axios.post('http://localhost:5000/api/session', payload, {
        headers: { Authorization: `Bearer ${token}` }
      });
      navigate(`/scan/${res.data.session_id}`);
    } catch (error) {
      console.error(error);
      alert('Error creating session');
    }
  };

  const handleLogout = () => {
    // Clear localStorage to log out the user
    localStorage.removeItem('token');
    // Navigate to login page
    navigate('/');
  };

  return (
    <div style={styles.container}>
      <div style={styles.card}>
        <h2 style={styles.title}>Create New Attendance Session</h2>
        <input
          style={styles.input}
          placeholder="Session Name"
          value={sessionName}
          onChange={e => setSessionName(e.target.value)}
        />
        <input
          style={styles.input}
          placeholder="Department"
          value={department}
          onChange={e => setDepartment(e.target.value)}
        />
        <select
          style={styles.input}
          value={academicYear}
          onChange={e => setAcademicYear(e.target.value)}
        >
          <option value="">Select Academic Year</option>
          <option value="2022">2022</option>
          <option value="2023">2023</option>
          <option value="2024">2024</option>
        </select>
        <button style={styles.buttonPrimary} onClick={createSession}>
          Start Scanning
        </button>
        <button style={styles.buttonSecondary} onClick={handleLogout}>
          Logout
        </button>
      </div>
    </div>
  );
}

const styles = {
  container: {
    minHeight: '100vh',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    background: '#f2f2f2',
    padding: '20px',
  },
  card: {
    background: '#fff',
    padding: '30px',
    borderRadius: '12px',
    boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
    width: '100%',
    maxWidth: '400px',
    textAlign: 'center'
  },
  title: {
    marginBottom: '20px',
    color: '#333'
  },
  input: {
    width: '100%',
    padding: '10px',
    margin: '10px 0',
    borderRadius: '6px',
    border: '1px solid #ccc',
    fontSize: '16px'
  },
  buttonPrimary: {
    width: '100%',
    padding: '10px',
    margin: '10px 0',
    border: 'none',
    borderRadius: '6px',
    backgroundColor: '#4CAF50',
    color: '#fff',
    fontSize: '16px',
    cursor: 'pointer'
  },
  buttonSecondary: {
    width: '100%',
    padding: '10px',
    border: 'none',
    borderRadius: '6px',
    backgroundColor: '#2196F3',
    color: '#fff',
    fontSize: '16px',
    cursor: 'pointer'
  }
};

export default Dashboard;
