import React, { useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import * as XLSX from 'xlsx';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function Scan() {
  const { sessionId } = useParams();
  const navigate = useNavigate();
  const [id, setId] = useState('');
  const [scannedStudents, setScannedStudents] = useState([]);

  // Function to handle barcode scan (via keyboard or Bluetooth)
  const handleScan = async () => {
    if (!id.trim()) return;

    // Check if the student is already scanned (based on USN)
    const existingStudent = scannedStudents.find(student => student.student_usn === id.trim());

    if (existingStudent) {
      toast.warn('‚ö†Ô∏è Student already scanned', { autoClose: 2000 });
      setId('');
      return;
    }

    try {
      const res = await axios.post('http://localhost:5000/api/scan', {
        session_id: sessionId,
        id_number: id,
      });
      toast.success(`‚úÖ Scanned: ${res.data.student_usn}`, { autoClose: 2000 });
      setScannedStudents(prev => [...prev, res.data]); // Add new student to the list
      setId('');
    } catch (error) {
      console.error(error);
      toast.error('‚ùå Error scanning student', { autoClose: 3000 });
    }
  };

  const handleEndSession = async () => {
    try {
      await axios.put(`http://localhost:5000/api/end-session/${sessionId}`);
      toast.success('üõë Session ended successfully', { autoClose: 2000 });
      setTimeout(() => navigate('/dashboard'), 2000);
    } catch (error) {
      console.error(error);
      toast.error('‚ùå Error ending session', { autoClose: 3000 });
    }
  };

  const handleLogout = () => {
    localStorage.clear();
    navigate('/');
  };

  const handleExport = () => {
    if (scannedStudents.length === 0) {
      toast.warn('‚ö†Ô∏è No students to export', { autoClose: 2000 });
      return;
    }

    const dataToExport = scannedStudents.map((student, index) => ({
      No: index + 1,
      Name: student.student_name,
      USN: student.student_usn,
      Department: student.student_department,
      Time: student.timestamp,
    }));

    const worksheet = XLSX.utils.json_to_sheet(dataToExport);
    const workbook = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(workbook, worksheet, "ScannedStudents");
    XLSX.writeFile(workbook, "Scanned_Students.xlsx");

    toast.success('üìÑ Excel file exported!', { autoClose: 2000 });
  };

  // Handle barcode input via the Bluetooth scanner
  const handleBarcodeInput = (e) => {
    setId(e.target.value);

    // If the user presses "Enter" (this is usually the behavior after a scan)
    if (e.key === 'Enter') {
      handleScan();
    }
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '50px' }}>
      <ToastContainer position="top-center" />

      <h2>üì∑ Scan Students</h2>

      <input
        style={inputStyle}
        placeholder="Scan Student ID Number"
        value={id}
        onChange={handleBarcodeInput} // Handle change
        onKeyDown={handleBarcodeInput} // Listen for keydown to trigger scan when 'Enter' is pressed
      />
      <br /><br />

      <button onClick={handleScan} style={buttonStyle}>‚úÖ Scan</button> &nbsp;
      <button onClick={handleEndSession} style={{ ...buttonStyle, backgroundColor: 'orange' }}>üõë End Session</button> <br /><br />
      <button onClick={handleExport} style={{ ...buttonStyle, backgroundColor: 'green' }}>üìÑ Export to Excel</button> &nbsp;
      <button onClick={handleLogout} style={{ ...buttonStyle, backgroundColor: 'red' }}>üö™ Logout</button>

      <div style={{ marginTop: '40px' }}>
        <h3>üìù Scanned Students</h3>
        {scannedStudents.length === 0 ? (
          <p>No students scanned yet.</p>
        ) : (
          <table style={tableStyle}>
            <thead>
              <tr>
                <th>No</th>
                <th>Name</th>
                <th>USN</th>
                <th>Department</th>
                <th>Timestamp</th>
              </tr>
            </thead>
            <tbody>
              {scannedStudents.map((student, index) => (
                <tr key={index}>
                  <td>{index + 1}</td>
                  <td>{student.student_name}</td>
                  <td>{student.student_usn}</td>
                  <td>{student.student_department}</td>
                  <td>{student.timestamp}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

// Styles
const inputStyle = {
  padding: '10px',
  width: '300px',
  fontSize: '16px',
};

const buttonStyle = {
  padding: '10px 20px',
  fontSize: '16px',
  margin: '5px',
  border: 'none',
  borderRadius: '8px',
  backgroundColor: '#007bff',
  color: 'white',
  cursor: 'pointer',
};

const tableStyle = {
  margin: 'auto',
  marginTop: '20px',
  borderCollapse: 'collapse',
  width: '80%',
};

export default Scan;
