import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../App.css';
import logo from '../images/logo.png';

function Login() {
  const [name, setName] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async () => {
    if (!name || !password) {
      alert('Please enter both name and password');
      return;
    }

    try {
      const res = await axios.post('http://localhost:5000/api/login', { name, password });
      localStorage.setItem('token', res.data.token);
      navigate('/dashboard');
    } catch (error) {
      alert('Invalid Credentials');
    }
  };

  return (
    <div>
      <header style={styles.header}>
        <img src={logo} alt="Logo" style={styles.logo} />

      </header>

      <div style={styles.container}>
        <div style={styles.card}>
          <h2 style={styles.title}>Department Head Login</h2>
          <input
            style={styles.input}
            placeholder="Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <input
            style={styles.input}
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <button style={styles.button} onClick={handleLogin}>Login</button>
        </div>
      </div>
    </div>
  );
}

const styles = {
  header: { display: 'flex', alignItems: 'center', justifyContent: 'center' },
  logo: { width: '300px',flex:lef },
  headerText: { fontSize: '2rem', margin: 0 },
  container: { minHeight: '100vh', display: 'flex', justifyContent: 'center', alignItems: 'center', background: '#f2f2f2' },
  card: { background: '#fff', padding: '30px', borderRadius: '12px', boxShadow: '0 4px 8px rgba(0,0,0,0.1)', width: '100%', maxWidth: '400px', textAlign: 'center' },
  title: { marginBottom: '20px', color: '#333' },
  input: { width: '100%', padding: '10px', margin: '10px 0', borderRadius: '6px', border: '1px solid #ccc', fontSize: '16px' },
  button: { width: '100%', padding: '10px', marginTop: '15px', border: 'none', borderRadius: '6px', backgroundColor: '#4CAF50', color: '#fff', fontSize: '16px', cursor: 'pointer' }
};

export default Login;
