import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../App.css';
import logo from '../images/logo.png';

function Dashboard() {
  const [sessionName, setSessionName] = useState('');
  const navigate = useNavigate();

  const createSession = async () => {
    if (!sessionName.trim()) {
      alert('Please enter a session name.');
      return;
    }

    try {
      const token = localStorage.getItem('token');
      const payload = {
        session_name: sessionName,
        started_by: 1,
      };
      const res = await axios.post('http://localhost:5000/api/session', payload, {
        headers: { Authorization: `Bearer ${token}` }
      });
      navigate(`/scan/${res.data.session_id}`);
    } catch (error) {
      console.error(error);
      alert('Error creating session');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/');
  };

  return (
    <div style={styles.page}>
      <img src={logo} alt="Logo" style={styles.logo} />
      <h2 style={styles.title}>Create New Attendance Session</h2>

      <div style={styles.form}>
        <input
          style={styles.input}
          placeholder="Session Name"
          value={sessionName}
          onChange={(e) => setSessionName(e.target.value)}
        />
        <button style={styles.buttonPrimary} onClick={createSession}>Start Scanning</button>
        <button style={styles.buttonSecondary} onClick={handleLogout}>Logout</button>
      </div>
    </div>
  );
}

const styles = {
  page: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    minHeight: '100vh',
    backgroundColor: '#f5f7fa',
    fontFamily: "'Segoe UI', sans-serif",
    padding: '20px'
  },
  logo: {
    width: '220px',
    marginBottom: '10px',
    margintop
  },
  title: {
    fontSize: '24px',
    color: '#222',
    marginBottom: '30px',
  },
  form: {
    width: '100%',
    maxWidth: '400px',
    display: 'flex',
    flexDirection: 'column',
    gap: '15px',
  },
  input: {
    padding: '12px',
    border: '1px solid #ccc',
    borderRadius: '6px',
    fontSize: '16px',
    backgroundColor: '#fff',
  },
  buttonPrimary: {
    padding: '12px',
    backgroundColor: '#0b3558',
    color: '#fff',
    fontWeight: '600',
    fontSize: '16px',
    border: 'none',
    borderRadius: '6px',
    cursor: 'pointer',
  },
  buttonSecondary: {
    padding: '12px',
    backgroundColor: '#2196F3',
    color: '#fff',
    fontWeight: '600',
    fontSize: '16px',
    border: 'none',
    borderRadius: '6px',
    cursor: 'pointer',
  },
};

export default Dashboard;
